import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useWallet } from '../context/WalletContext';
import { FaGraduationCap, FaWallet, FaBars, FaTimes, FaUser, FaSignOutAlt, FaSignInAlt, FaUserPlus } from 'react-icons/fa';
import { getCurrentUser, logoutUser } from '../utils/userUtils';
import toast from 'react-hot-toast';

const Nav = styled.nav`
  background: linear-gradient(135deg, rgba(255,255,255,0.08) 0%, rgba(83,52,131,0.05) 100%);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(255,255,255,0.15);
  position: sticky;
  top: 0;
  z-index: 1000;
  padding: 1rem 0;
  box-shadow: 0 4px 32px 0 rgba(83,52,131,0.2);
`;

const NavContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled(Link)`
  display: flex;
  align-items: center;
  text-decoration: none;
  color: white;
  font-size: 1.5rem;
  font-weight: 700;
  
  .logo-icon {
    margin-right: 0.5rem;
    color: #533483;
  }
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  gap: 2rem;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled(Link)`
  color: ${props => props.active ? '#533483' : 'white'};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;
  position: relative;

  &:hover {
    color: #533483;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: ${props => props.active ? '100%' : '0'};
    height: 2px;
    background: linear-gradient(90deg, #533483, #0f3460);
    transition: width 0.3s ease;
  }

  &:hover::after {
    width: 100%;
  }
`;

const WalletButton = styled(motion.button)`
  background: linear-gradient(90deg, #533483, #0f3460, #16213e, #1a1a2e);
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(83,52,131,0.4);
  }

  &:disabled {
    opacity: 0.7;
    cursor: not-allowed;
    transform: none;
  }
`;

const AccountInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  color: white;
  font-size: 0.9rem;

  .account-address {
    background: rgba(255, 255, 255, 0.1);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-family: monospace;
  }
`;

const AuthButtons = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const LoginButton = styled(Link)`
  background: linear-gradient(90deg, #533483, #0f3460);
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;
  text-decoration: none;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(83,52,131,0.4);
    color: white;
  }
`;

const RegisterButton = styled(Link)`
  background: transparent;
  color: #533483;
  border: 2px solid #533483;
  padding: 0.75rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;
  text-decoration: none;

  &:hover {
    background: #533483;
    color: white;
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(83,52,131,0.4);
  }
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  color: white;
  font-size: 0.9rem;

  .user-name {
    background: rgba(255, 255, 255, 0.1);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-weight: 500;
  }
`;

const LogoutButton = styled.button`
  background: rgba(255, 255, 255, 0.1);
  color: white;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  font-size: 0.9rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }
`;

const MobileMenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;

  @media (max-width: 768px) {
    display: block;
  }
`;

const MobileMenu = styled(motion.div)`
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.95);
  backdrop-filter: blur(10px);
  z-index: 1001;
  padding: 2rem;

  @media (max-width: 768px) {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 2rem;
  }
`;

const MobileNavLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-size: 1.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    color: #533483;
  }
`;

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const { isConnected, account, connectWallet, disconnectWallet, isLoading } = useWallet();
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const user = getCurrentUser();
    setCurrentUser(user);
  }, [location]);

  useEffect(() => {
    const user = getCurrentUser();
    const wasWalletConnected = localStorage.getItem('isWalletConnected') === 'true';
    if (user && wasWalletConnected && !isConnected && !isLoading) {
      connectWallet();
    }
    // eslint-disable-next-line
  }, [currentUser, isConnected, isLoading]);

  useEffect(() => {
    if (isConnected) {
      localStorage.setItem('isWalletConnected', 'true');
    } else {
      localStorage.setItem('isWalletConnected', 'false');
    }
  }, [isConnected]);

  const navItems = [
    { path: '/', label: 'Trang chủ' },
    { path: '/dashboard', label: 'Dashboard' },
    { path: '/learnpass', label: 'LearnPass' },
    { path: '/marketplace', label: 'Marketplace' },
    { path: '/badges', label: 'Badges' },
    { path: '/verify', label: 'Xác minh' },
    { path: '/about', label: 'Giới thiệu' },
  ];

  const handleWalletAction = () => {
    if (isConnected) {
      disconnectWallet();
    } else {
      connectWallet();
    }
  };

  const handleLogout = () => {
    logoutUser();
    setCurrentUser(null);
    toast.success('Đăng xuất thành công!');
    navigate('/');
  };

  const formatAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <Nav>
      <NavContainer>
        <Logo to="/">
          <FaGraduationCap className="logo-icon" />
          EduWallet
        </Logo>

        <NavMenu>
          {navItems.map((item) => (
            <NavLink
              key={item.path}
              to={item.path}
              active={location.pathname === item.path}
            >
              {item.label}
            </NavLink>
          ))}
        </NavMenu>

        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          {currentUser ? (
            <>
              <UserInfo>
                <span className="user-name">
                  <FaUser style={{ marginRight: '0.5rem' }} />
                  {currentUser.name}
                </span>
                <LogoutButton onClick={handleLogout}>
                  <FaSignOutAlt />
                  Đăng xuất
                </LogoutButton>
              </UserInfo>

              {isConnected ? (
                <AccountInfo>
                  <span className="account-address">
                    {formatAddress(account)}
                  </span>
                  <WalletButton
                    onClick={handleWalletAction}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <FaWallet />
                    Ngắt kết nối
                  </WalletButton>
                </AccountInfo>
              ) : (
                <WalletButton
                  onClick={handleWalletAction}
                  disabled={isLoading}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <FaWallet />
                  {isLoading ? 'Đang kết nối...' : 'Kết nối ví'}
                </WalletButton>
              )}
            </>
          ) : (
            <AuthButtons>
              <LoginButton to="/login">
                <FaSignInAlt />
                Đăng nhập
              </LoginButton>
              <RegisterButton to="/register">
                <FaUserPlus />
                Đăng ký
              </RegisterButton>
            </AuthButtons>
          )}

          <MobileMenuButton onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
            {isMobileMenuOpen ? <FaTimes /> : <FaBars />}
          </MobileMenuButton>
        </div>
      </NavContainer>

      {isMobileMenuOpen && (
        <MobileMenu
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          {navItems.map((item) => (
            <MobileNavLink
              key={item.path}
              to={item.path}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              {item.label}
            </MobileNavLink>
          ))}
          
          {currentUser ? (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', marginTop: '2rem' }}>
              <div style={{ color: 'white', textAlign: 'center' }}>
                Xin chào, {currentUser.name}!
              </div>
              <LogoutButton onClick={() => {
                handleLogout();
                setIsMobileMenuOpen(false);
              }}>
                <FaSignOutAlt />
                Đăng xuất
              </LogoutButton>
              
              {isConnected ? (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', marginTop: '1rem' }}>
                  <div style={{ color: 'white', textAlign: 'center', fontSize: '0.9rem' }}>
                    Ví: {formatAddress(account)}
                  </div>
                  <WalletButton
                    onClick={() => {
                      handleWalletAction();
                      setIsMobileMenuOpen(false);
                    }}
                  >
                    <FaWallet />
                    Ngắt kết nối
                  </WalletButton>
                </div>
              ) : (
                <WalletButton
                  onClick={() => {
                    handleWalletAction();
                    setIsMobileMenuOpen(false);
                  }}
                  style={{ marginTop: '1rem' }}
                >
                  <FaWallet />
                  {isLoading ? 'Đang kết nối...' : 'Kết nối ví'}
                </WalletButton>
              )}
            </div>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', marginTop: '2rem' }}>
              <LoginButton to="/login" onClick={() => setIsMobileMenuOpen(false)}>
                <FaSignInAlt />
                Đăng nhập
              </LoginButton>
              <RegisterButton to="/register" onClick={() => setIsMobileMenuOpen(false)}>
                <FaUserPlus />
                Đăng ký
              </RegisterButton>
            </div>
          )}
        </MobileMenu>
      )}
    </Nav>
  );
};

export default Navbar;
