import React from 'react';
import styled from 'styled-components';
import demoData from '../data/demoData.json';
import { Link } from 'react-router-dom';

const Container = styled.div`
  max-width: 1100px;
  margin: 0 auto;
  padding: 2rem 0;
`;

const Section = styled.section`
  margin-bottom: 2.5rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.5rem;
  color: #667eea;
  margin-bottom: 1rem;
`;

const CardRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1.5rem;
`;

const Card = styled.div`
  background: rgba(255,255,255,0.07);
  border-radius: 14px;
  padding: 1.5rem;
  min-width: 220px;
  flex: 1 1 220px;
  color: #fff;
  box-shadow: 0 2px 12px rgba(102,126,234,0.08);
`;

const Avatar = styled.img`
  width: 64px;
  height: 64px;
  border-radius: 50%;
  margin-bottom: 1rem;
`;

const Dashboard = () => {
  const { user, learnPass, certificates, badges, marketplace } = demoData;
  return (
    <Container>
      <Section>
        <SectionTitle>Thông tin cá nhân</SectionTitle>
        <CardRow>
          <Card style={{ maxWidth: 300 }}>
            <Avatar src={user.avatar} alt={user.name} />
            <div><b>{user.name}</b></div>
            <div>{user.email}</div>
            <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>Ví: {user.wallet}</div>
            <Link to="/learnpass" className="btn btn-primary" style={{ marginTop: 12 }}>Xem LearnPass</Link>
          </Card>
        </CardRow>
      </Section>
      <Section>
        <SectionTitle>Chứng chỉ học tập</SectionTitle>
        <CardRow>
          {certificates.map(cert => (
            <Card key={cert.id}>
              <img src={cert.image} alt={cert.title} style={{ width: '100%', borderRadius: 8, marginBottom: 8 }} />
              <div><b>{cert.title}</b></div>
              <div style={{ fontSize: '0.95rem', color: '#c3bfff' }}>{cert.issuer}</div>
              <div>Điểm: {cert.score}</div>
              <div>Kỹ năng: {cert.skills.join(', ')}</div>
              <div style={{ fontSize: '0.9rem', opacity: 0.7 }}>Ngày cấp: {cert.date}</div>
            </Card>
          ))}
        </CardRow>
      </Section>
      <Section>
        <SectionTitle>Badge thành tích</SectionTitle>
        <CardRow>
          {badges.map(badge => (
            <Card key={badge.id}>
              <img src={badge.image} alt={badge.title} style={{ width: 60, height: 60, borderRadius: 30, marginBottom: 8 }} />
              <div><b>{badge.title}</b></div>
              <div style={{ color: '#ffd700', fontWeight: 500 }}>{badge.reward}</div>
            </Card>
          ))}
        </CardRow>
      </Section>
      <Section>
        <SectionTitle>Marketplace nổi bật</SectionTitle>
        <CardRow>
          {marketplace.map(item => (
            <Card key={item.id}>
              <img src={item.image} alt={item.title} style={{ width: '100%', borderRadius: 8, marginBottom: 8 }} />
              <div><b>{item.title}</b></div>
              <div style={{ fontSize: '0.95rem', color: '#c3bfff' }}>{item.description}</div>
              <div style={{ margin: '8px 0' }}>Giá: <b>{item.price} {item.currency}</b></div>
              <button className="btn btn-primary" disabled>Đổi thưởng</button>
            </Card>
          ))}
        </CardRow>
      </Section>
    </Container>
  );
};

export default Dashboard;
