import React, { useState } from 'react';
import styled from 'styled-components';
import demoData from '../data/demoData.json';
import { Link } from 'react-router-dom';
import { useWallet } from '../context/WalletContext';

const Container = styled.div`
  max-width: 1100px;
  margin: 0 auto;
  padding: 2rem 0;
`;

const Section = styled.section`
  margin-bottom: 2.5rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.5rem;
  color: #667eea;
  margin-bottom: 1rem;
`;

const CardRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1.5rem;
`;

const Card = styled.div`
  background: rgba(255,255,255,0.07);
  border-radius: 14px;
  padding: 1.5rem;
  min-width: 220px;
  flex: 1 1 220px;
  color: #fff;
  box-shadow: 0 2px 12px rgba(102,126,234,0.08);
`;

const Avatar = styled.img`
  width: 64px;
  height: 64px;
  border-radius: 50%;
  margin-bottom: 1rem;
`;

const TokenBalance = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 12px;
  padding: 1rem;
  text-align: center;
  color: white;
  margin-bottom: 1rem;
`;

const Dashboard = () => {
  const { user, certificates, badges, marketplace } = demoData;
  const { isConnected, account } = useWallet();
  const [userBalance] = useState(50); // Demo balance

  return (
    <Container>
      <Section>
        <SectionTitle>Thông tin cá nhân</SectionTitle>
        <CardRow>
          <Card style={{ maxWidth: 300 }}>
            <Avatar src={user.avatar} alt={user.name} />
            <div><b>{user.name}</b></div>
            <div>{user.email}</div>
            {isConnected ? (
              <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>
                Ví: {account?.slice(0, 6)}...{account?.slice(-4)}
              </div>
            ) : (
              <div style={{ fontSize: '0.9rem', color: '#ff6b6b' }}>
                Chưa kết nối ví
              </div>
            )}
            <Link to="/learnpass" className="btn btn-primary" style={{ marginTop: 12 }}>Xem LearnPass</Link>
          </Card>
          
          {isConnected && (
            <Card style={{ maxWidth: 300 }}>
              <TokenBalance>
                <div style={{ fontSize: '1.2rem', fontWeight: 'bold', marginBottom: 8 }}>
                  Số dư EDU Token
                </div>
                <div style={{ fontSize: '2rem', fontWeight: 'bold' }}>
                  {userBalance} EDU
                </div>
                <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>
                  Có thể đổi lấy voucher, phần thưởng
                </div>
              </TokenBalance>
              <Link to="/marketplace" className="btn btn-secondary" style={{ width: '100%' }}>
                Đi đến Marketplace
              </Link>
            </Card>
          )}
        </CardRow>
      </Section>
      <Section>
        <SectionTitle>Chứng chỉ học tập</SectionTitle>
        <CardRow>
          {certificates.map(cert => (
            <Card key={cert.id}>
              <img src={cert.image} alt={cert.title} style={{ width: '100%', borderRadius: 8, marginBottom: 8 }} />
              <div><b>{cert.title}</b></div>
              <div style={{ fontSize: '0.95rem', color: '#c3bfff' }}>{cert.issuer}</div>
              <div>Điểm: {cert.score}</div>
              <div>Kỹ năng: {cert.skills.join(', ')}</div>
              <div style={{ fontSize: '0.9rem', opacity: 0.7 }}>Ngày cấp: {cert.date}</div>
            </Card>
          ))}
        </CardRow>
      </Section>
      <Section>
        <SectionTitle>Badge thành tích</SectionTitle>
        <CardRow>
          {badges.map(badge => (
            <Card key={badge.id}>
              <img src={badge.image} alt={badge.title} style={{ width: 60, height: 60, borderRadius: 30, marginBottom: 8 }} />
              <div><b>{badge.title}</b></div>
              <div style={{ color: '#ffd700', fontWeight: 500 }}>{badge.reward}</div>
            </Card>
          ))}
        </CardRow>
      </Section>
      <Section>
        <SectionTitle>Marketplace nổi bật</SectionTitle>
        <CardRow>
          {marketplace.map(item => (
            <Card key={item.id}>
              <img src={item.image} alt={item.title} style={{ width: '100%', borderRadius: 8, marginBottom: 8 }} />
              <div><b>{item.title}</b></div>
              <div style={{ fontSize: '0.95rem', color: '#c3bfff' }}>{item.description}</div>
              <div style={{ margin: '8px 0' }}>Giá: <b>{item.price} {item.currency}</b></div>
              <Link to="/marketplace" className="btn btn-primary">Xem chi tiết</Link>
            </Card>
          ))}
        </CardRow>
      </Section>
    </Container>
  );
};

export default Dashboard;
