  FaUser, 
  FaGraduationCap, 
  FaMedal, 
  FaStore, 
  FaCertificate,
  FaTrophy,
  FaCoins,
  FaWallet,
  FaCheckCircle
} from 'react-icons/fa';

const Container = styled.div`
  max-width: 1100px;
  margin: 0 auto;
  padding: 2rem 0;
`;

const Section = styled.section`
  margin-bottom: 2.5rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.5rem;
  color: #667eea;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const CardRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1.5rem;
`;

const Card = styled.div`
  background: rgba(255,255,255,0.07);
  border-radius: 14px;
  padding: 1.5rem;
  min-width: 220px;
  flex: 1 1 220px;
  color: #fff;
  box-shadow: 0 2px 12px rgba(102,126,234,0.08);
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 25px rgba(102,126,234,0.15);
  }
`;

const Avatar = styled.div`
  width: 64px;
  height: 64px;
  border-radius: 50%;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  color: white;
`;

const TokenBalance = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 12px;
  padding: 1rem;
  text-align: center;
  color: white;
  margin-bottom: 1rem;
`;

const CertificateCard = styled(Card)`
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #667eea, #764ba2);
  }
`;

const BadgeCard = styled(Card)`
  text-align: center;
  background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%);
`;

const BadgeIcon = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  margin: 0 auto 1rem;
  background: linear-gradient(135deg, #ffd700 0%, #ffed4e 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  color: #333;
`;

const MarketplaceCard = styled(Card)`
  text-align: center;
`;

const MarketplaceIcon = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 12px;
  margin: 0 auto 1rem;
  background: linear-gradient(135deg, #4CAF50 0%, #45a049 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  color: white;
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  margin-bottom: 2rem;
`;

const StatCard = styled.div`
  background: rgba(255,255,255,0.05);
  border-radius: 12px;
  padding: 1rem;
  text-align: center;
  color: white;
`;

const Dashboard = () => {
  const { user, certificates, badges, marketplace } = demoData;
  const { isConnected, account } = useWallet();
  const [userBalance] = useState(50);

  return (
    <Container>
      <Section>
        <SectionTitle>
          <FaUser /> Thông tin cá nhân
        </SectionTitle>
        <CardRow>
          <Card style={{ maxWidth: 300 }}>
            <Avatar>
              <FaUser />
            </Avatar>
            <div><b>{user.name}</b></div>
            <div>{user.email}</div>
            {isConnected ? (
              <div style={{ fontSize: '0.9rem', opacity: 0.8, display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <FaWallet /> {account?.slice(0, 6)}...{account?.slice(-4)}
              </div>
            ) : (
              <div style={{ fontSize: '0.9rem', color: '#ff6b6b', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                <FaWallet /> Chưa kết nối ví
              </div>
            )}
            <Link to="/learnpass" className="btn btn-primary" style={{ marginTop: 12, display: 'flex', alignItems: 'center', gap: '0.5rem', justifyContent: 'center' }}>
              <FaGraduationCap /> Xem LearnPass
            </Link>
          </Card>
          
          {isConnected && (
            <Card style={{ maxWidth: 300 }}>
              <TokenBalance>
                <div style={{ fontSize: '1.2rem', fontWeight: 'bold', marginBottom: 8, display: 'flex', alignItems: 'center', gap: '0.5rem', justifyContent: 'center' }}>
                  <FaCoins /> Số dư EDU Token
                </div>
                <div style={{ fontSize: '2rem', fontWeight: 'bold' }}>
                  {userBalance} EDU
                </div>
                <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>
                  Có thể đổi lấy voucher, phần thưởng
                </div>
              </TokenBalance>
              <Link to="/marketplace" className="btn btn-secondary" style={{ width: '100%', display: 'flex', alignItems: 'center', gap: '0.5rem', justifyContent: 'center' }}>
                <FaStore /> Đi đến Marketplace
              </Link>
            </Card>
          )}
        </CardRow>
      </Section>

      <Section>
        <StatsGrid>
          <StatCard>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#667eea' }}>{certificates.length}</div>
            <div>Chứng chỉ</div>
          </StatCard>
          <StatCard>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#ffd700' }}>{badges.length}</div>
            <div>Badge</div>
          </StatCard>
          <StatCard>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#4CAF50' }}>{marketplace.length}</div>
            <div>Voucher có sẵn</div>
          </StatCard>
        </StatsGrid>
      </Section>

      <Section>
        <SectionTitle>
          <FaCertificate /> Chứng chỉ học tập
        </SectionTitle>
        <CardRow>
          {certificates.map(cert => (
            <CertificateCard key={cert.id}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
                <div style={{ fontSize: '2rem', color: '#667eea' }}>
                  <FaCertificate />
                </div>
                <div>
                  <div><b>{cert.title}</b></div>
                  <div style={{ fontSize: '0.9rem', color: '#c3bfff' }}>{cert.issuer}</div>
                </div>
              </div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem' }}>
                <FaCheckCircle style={{ color: '#4CAF50' }} />
                Điểm: {cert.score}
              </div>
              <div style={{ fontSize: '0.9rem', marginBottom: '0.5rem' }}>
                Kỹ năng: {cert.skills.join(', ')}
              </div>
              <div style={{ fontSize: '0.9rem', opacity: 0.7 }}>Ngày cấp: {cert.date}</div>
            </CertificateCard>
          ))}
        </CardRow>
      </Section>

      <Section>
        <SectionTitle>
          <FaMedal /> Badge thành tích
        </SectionTitle>
        <CardRow>
          {badges.map(badge => (
            <BadgeCard key={badge.id}>
              <BadgeIcon>
                <FaTrophy />
              </BadgeIcon>
              <div><b>{badge.title}</b></div>
              <div style={{ color: '#ffd700', fontWeight: 500 }}>{badge.reward}</div>
            </BadgeCard>
          ))}
        </CardRow>
      </Section>

      <Section>
        <SectionTitle>
          <FaStore /> Marketplace nổi bật
        </SectionTitle>
        <CardRow>
          {marketplace.map(item => (
            <MarketplaceCard key={item.id}>
              <MarketplaceIcon>
                <FaStore />
              </MarketplaceIcon>
              <div><b>{item.title}</b></div>
              <div style={{ fontSize: '0.95rem', color: '#c3bfff' }}>{item.description}</div>
              <div style={{ margin: '8px 0', display: 'flex', alignItems: 'center', gap: '0.5rem', justifyContent: 'center' }}>
                <FaCoins /> Giá: <b>{item.price} {item.currency}</b>
              </div>
              <Link to="/marketplace" className="btn btn-primary" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', justifyContent: 'center' }}>
                <FaStore /> Xem chi tiết
              </Link>
            </MarketplaceCard>
          ))}
        </CardRow>
      </Section>
    </Container>
  );
};

export default Dashboard;
