import React from 'react';
import styled from 'styled-components';
import demoData from '../data/demoData.json';

const Container = styled.div`
  max-width: 900px;
  margin: 0 auto;
  padding: 2rem 0;
`;

const NFTCard = styled.div`
  background: rgba(255,255,255,0.08);
  border-radius: 16px;
  padding: 2rem;
  display: flex;
  gap: 2rem;
  align-items: center;
  margin-bottom: 2rem;
  color: #fff;
`;

const NFTImage = styled.img`
  width: 180px;
  height: 180px;
  border-radius: 16px;
  object-fit: cover;
  background: #222;
`;

const Meta = styled.div`
  flex: 1;
`;

const Section = styled.section`
  margin-bottom: 2rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.3rem;
  color: #667eea;
  margin-bottom: 1rem;
`;

const List = styled.ul`
  list-style: none;
  padding: 0;
`;

const ListItem = styled.li`
  margin-bottom: 0.7rem;
  background: rgba(255,255,255,0.04);
  border-radius: 8px;
  padding: 0.7rem 1rem;
`;

const LearnPass = () => {
  const { user, learnPass, certificates } = demoData;
  return (
    <Container>
      <NFTCard>
        <NFTImage src={learnPass.nftImage} alt="LearnPass NFT" />
        <Meta>
          <h2>LearnPass NFT của {user.name}</h2>
          <div style={{ fontSize: '1.1rem', color: '#c3bfff', marginBottom: 8 }}>ID: {learnPass.id}</div>
          <div>Chủ sở hữu: <b>{user.name}</b> ({user.wallet})</div>
        </Meta>
      </NFTCard>
      <Section>
        <SectionTitle>Khóa học & kỹ năng</SectionTitle>
        <List>
          {learnPass.metadata.courses.map((course, idx) => (
            <ListItem key={idx}>
              <b>{course.name}</b> - Điểm: {course.score} <br/>
              Kỹ năng: {course.skills.join(', ')}<br/>
              Ngày hoàn thành: {course.date} <br/>
              Tổ chức cấp: {course.issuer}
            </ListItem>
          ))}
        </List>
      </Section>
      <Section>
        <SectionTitle>Hoạt động ngoại khoá</SectionTitle>
        <List>
          {learnPass.metadata.extracurricular.map((act, idx) => (
            <ListItem key={idx}>
              {act.name} ({act.date})
            </ListItem>
          ))}
        </List>
      </Section>
      <Section>
        <SectionTitle>Lịch sử tín chỉ</SectionTitle>
        <List>
          {learnPass.metadata.creditHistory.map((credit, idx) => (
            <ListItem key={idx}>
              {credit.type === 'course' ? 'Khóa học' : 'Khác'}: <b>{credit.name}</b> - {credit.credit} tín chỉ
            </ListItem>
          ))}
        </List>
      </Section>
      <Section>
        <SectionTitle>Chứng chỉ liên quan</SectionTitle>
        <List>
          {certificates.map(cert => (
            <ListItem key={cert.id}>
              <img src={cert.image} alt={cert.title} style={{ width: 40, height: 40, borderRadius: 8, marginRight: 8, verticalAlign: 'middle' }} />
              <b>{cert.title}</b> - {cert.issuer} ({cert.date})
            </ListItem>
          ))}
        </List>
      </Section>
    </Container>
  );
};

export default LearnPass;
