import React from 'react';
import styled from 'styled-components';
import demoData from '../data/demoData.json';

const Container = styled.div`
  max-width: 900px;
  margin: 0 auto;
  padding: 2rem 0;
`;

const Grid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 2rem;
`;

const BadgeCard = styled.div`
  background: rgba(255,255,255,0.08);
  border-radius: 16px;
  padding: 2rem 1.5rem;
  min-width: 220px;
  max-width: 260px;
  text-align: center;
  color: #fff;
  box-shadow: 0 4px 24px rgba(102,126,234,0.08);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const BadgeImage = styled.img`
  width: 80px;
  height: 80px;
  border-radius: 50%;
  margin-bottom: 1rem;
`;

const Badges = () => {
  const { badges } = demoData;
  return (
    <Container>
      <h2 style={{ color: '#667eea', marginBottom: 24 }}>Danh hiệu & phần thưởng</h2>
      <Grid>
        {badges.map(badge => (
          <BadgeCard key={badge.id}>
            <BadgeImage src={badge.image} alt={badge.title} />
            <div style={{ fontWeight: 700, fontSize: '1.1rem', marginBottom: 8 }}>{badge.title}</div>
            <div style={{ color: '#ffd700', fontWeight: 500 }}>{badge.reward}</div>
          </BadgeCard>
        ))}
      </Grid>
    </Container>
  );
};

export default Badges;
