import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useWallet } from '../context/WalletContext';
import { FaGraduationCap, FaWallet, FaBars, FaTimes } from 'react-icons/fa';

const Nav = styled.nav`
  background: linear-gradient(135deg, rgba(255,255,255,0.12) 0%, rgba(102,126,234,0.08) 100%);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(255,255,255,0.2);
  position: sticky;
  top: 0;
  z-index: 1000;
  padding: 1rem 0;
  box-shadow: 0 4px 32px 0 rgba(102,126,234,0.2);
`;

const NavContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled(Link)`
  display: flex;
  align-items: center;
  text-decoration: none;
  color: white;
  font-size: 1.5rem;
  font-weight: 700;
  
  .logo-icon {
    margin-right: 0.5rem;
    color: #f093fb;
  }
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  gap: 2rem;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled(Link)`
  color: ${props => props.active ? '#f093fb' : 'white'};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;
  position: relative;

  &:hover {
    color: #f093fb;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: ${props => props.active ? '100%' : '0'};
    height: 2px;
    background: linear-gradient(90deg, #667eea, #f093fb);
    transition: width 0.3s ease;
  }

  &:hover::after {
    width: 100%;
  }
`;

const WalletButton = styled(motion.button)`
  background: linear-gradient(90deg, #667eea, #764ba2, #f093fb, #f5576c, #4facfe);
  background-size: 400% 400%;
  animation: galaxy-btn 8s linear infinite;
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(240,147,251,0.4);
  }

  &:disabled {
    opacity: 0.7;
    cursor: not-allowed;
    transform: none;
  }
`;

const AccountInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  color: white;
  font-size: 0.9rem;

  .account-address {
    background: rgba(255, 255, 255, 0.1);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-family: monospace;
  }
`;

const MobileMenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;

  @media (max-width: 768px) {
    display: block;
  }
`;

const MobileMenu = styled(motion.div)`
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.95);
  backdrop-filter: blur(10px);
  z-index: 1001;
  padding: 2rem;

  @media (max-width: 768px) {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 2rem;
  }
`;

const MobileNavLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-size: 1.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    color: #f093fb;
  }
`;

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const { isConnected, account, connectWallet, disconnectWallet, isLoading } = useWallet();
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'Trang chủ' },
    { path: '/dashboard', label: 'Dashboard' },
    { path: '/learnpass', label: 'LearnPass' },
    { path: '/marketplace', label: 'Marketplace' },
    { path: '/badges', label: 'Badges' },
    { path: '/verify', label: 'Xác minh' },
    { path: '/about', label: 'Giới thiệu' },
  ];

  const handleWalletAction = () => {
    if (isConnected) {
      disconnectWallet();
    } else {
      connectWallet();
    }
  };

  const formatAddress = (address) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  return (
    <Nav>
      <NavContainer>
        <Logo to="/">
          <FaGraduationCap className="logo-icon" />
          EduWallet
        </Logo>

        <NavMenu>
          {navItems.map((item) => (
            <NavLink
              key={item.path}
              to={item.path}
              active={location.pathname === item.path}
            >
              {item.label}
            </NavLink>
          ))}
        </NavMenu>

        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          {isConnected ? (
            <AccountInfo>
              <span className="account-address">
                {formatAddress(account)}
              </span>
              <WalletButton
                onClick={handleWalletAction}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <FaWallet />
                Ngắt kết nối
              </WalletButton>
            </AccountInfo>
          ) : (
            <WalletButton
              onClick={handleWalletAction}
              disabled={isLoading}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FaWallet />
              {isLoading ? 'Đang kết nối...' : 'Kết nối ví'}
            </WalletButton>
          )}

          <MobileMenuButton onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}>
            {isMobileMenuOpen ? <FaTimes /> : <FaBars />}
          </MobileMenuButton>
        </div>
      </NavContainer>

      {isMobileMenuOpen && (
        <MobileMenu
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          {navItems.map((item) => (
            <MobileNavLink
              key={item.path}
              to={item.path}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              {item.label}
            </MobileNavLink>
          ))}
          <WalletButton
            onClick={() => {
              handleWalletAction();
              setIsMobileMenuOpen(false);
            }}
            style={{ marginTop: '2rem' }}
          >
            <FaWallet />
            {isConnected ? 'Ngắt kết nối' : 'Kết nối ví'}
          </WalletButton>
        </MobileMenu>
      )}
    </Nav>
  );
};

export default Navbar;
