import React from 'react';
import styled from 'styled-components';
import demoData from '../data/demoData.json';

const Container = styled.div`
  max-width: 900px;
  margin: 0 auto;
  padding: 2rem 0;
`;

const Grid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 2rem;
`;

const ItemCard = styled.div`
  background: rgba(255,255,255,0.08);
  border-radius: 16px;
  padding: 2rem 1.5rem;
  min-width: 220px;
  max-width: 260px;
  text-align: center;
  color: #fff;
  box-shadow: 0 4px 24px rgba(102,126,234,0.08);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ItemImage = styled.img`
  width: 80px;
  height: 80px;
  border-radius: 16px;
  margin-bottom: 1rem;
`;

const Marketplace = () => {
  const { marketplace } = demoData;
  return (
    <Container>
      <h2 style={{ color: '#667eea', marginBottom: 24 }}>Marketplace - Đổi thưởng</h2>
      <Grid>
        {marketplace.map(item => (
          <ItemCard key={item.id}>
            <ItemImage src={item.image} alt={item.title} />
            <div style={{ fontWeight: 700, fontSize: '1.1rem', marginBottom: 8 }}>{item.title}</div>
            <div style={{ color: '#c3bfff', marginBottom: 8 }}>{item.description}</div>
            <div style={{ margin: '8px 0' }}>Giá: <b>{item.price} {item.currency}</b></div>
            <button className="btn btn-primary" disabled>Đổi thưởng</button>
          </ItemCard>
        ))}
      </Grid>
    </Container>
  );
};

export default Marketplace;
